mapper.select:
SELECT * FROM auth_user



ORDER BY id DESC


  LIMIT #{skip}, #{size}

select ID, NAME, PASSWORD, CDT, UDT, AVATAR, ADDRESS, PHONE
from auth_user
where ID = #{id,jdbcType=INTEGER}

select ID, NAME, PASSWORD, CDT, UDT, AVATAR, ADDRESS, PHONE
from auth_user

  NAME =#{name}

select ID, NAME, PASSWORD, CDT, UDT, AVATAR, ADDRESS, PHONE
from auth_user
where id in 

  #{id}


  LIMIT #{skip}, #{size}
